sys_script_include.script:
var UniqueRecordIdentificationTool = Class.create();
UniqueRecordIdentificationTool.prototype = {
    initialize: function() {
		this.error_code = '';
    },

	// Parms:
	// --- gr_obj -> a glide_record object of the record to be checked
	// --- coalesce_fields -> a list of field names to check
	// returns:
	// --- true if unique
	// --- false if duplicate was found
	validateUniqueRecord: function(gr_obj, coalesce_fields) {
		var table = gr_obj.getValue('sys_class_name');
		var gr_check = new GlideRecord(table);
		var field_name = '';
		for (var i = 0; i < coalesce_fields.length; i++) {
			field_name = coalesce_fields[i];
			gr_check.addQuery(field_name, gr_obj.getValue(field_name));
		}
		gr_check.addQuery('sys_id', '!=', gr_obj.getUniqueValue());
		gr_check.query();

		var count = gr_check.getRowCount();
		if (count == 0) return true;

		this.error_code = count +' duplicate records found';
		this._logError(this.error_code);
		return false;
	},

	_logError: function(message) {
		gs.error('Attribute Extension Tool: ' + message);
	},

    type: 'UniqueRecordIdentificationTool'
};
